INSERT INTO `q_bank` VALUES (187, 'b', '分配给业余业务的某频段的频率下限为F1，业余电台实际可以工作的发信频率应为：', '[{\"name\":\"F1-信号下边带的频率宽度\"},{\"name\":\"F1\"},{\"name\":\"F1-2×信号下边带的频率宽度\"},{\"name\":\"F1+信号下边带的频率宽度\"}]', 3, 'LK0173', '', '2022-06-02 09:59:00', '2022-06-02 10:20:19', 173);
