INSERT INTO `q_bank` VALUES (894, 'a', '“我遇到天电干扰”的业余无线电通信Q简语为：', '[{\"name\":\"QRN\"},{\"name\":\"QSN\"},{\"name\":\"QRM\"},{\"name\":\"QST\"}]', 0, 'LK0294', '', '2022-06-02 10:04:54', '2022-06-02 10:20:21', 294);
INSERT INTO `q_bank` VALUES (895, 'a', '“我给你收据（QSL卡片）、我已收妥”的业余无线电通信Q简语为：', '[{\"name\":\"QRV\"},{\"name\":\"QSX\"},{\"name\":\"QRG\"},{\"name\":\"QSL\"}]', 3, 'LK0313', '', '2022-06-02 10:04:54', '2022-06-02 10:20:21', 313);
INSERT INTO `q_bank` VALUES (896, 'a', '“我的电台位置是××××”的业余无线电通信Q简语为：', '[{\"name\":\"QSL　××××\"},{\"name\":\"QSP　××××\"},{\"name\":\"QTH　××××\"},{\"name\":\"QRD　××××\"}]', 2, 'LK0321', '', '2022-06-02 10:04:54', '2022-06-02 10:20:21', 321);
INSERT INTO `q_bank` VALUES (897, 'a', '“天线”的业余无线电通信常用缩语是：', '[{\"name\":\"ATR\"},{\"name\":\"ATN\"},{\"name\":\"ANT\"},{\"name\":\"ATT\"}]', 2, 'LK0331', '“天线”的英文是 antenna【BI6LAL】', '2022-06-02 10:04:54', '2022-06-02 10:20:21', 331);
INSERT INTO `q_bank` VALUES (898, 'a', '业余无线电常用缩语“ARDF”的意思是：', '[{\"name\":\"业余无线电测向\"},{\"name\":\"天线测试仪\"},{\"name\":\"地址\"},{\"name\":\"天线调谐器、天调\"}]', 0, 'LK0332', '', '2022-06-02 10:04:55', '2022-06-02 10:20:21', 332);
INSERT INTO `q_bank` VALUES (899, 'a', '“频率”的业余无线电通信常用缩语是：', '[{\"name\":\"FER\"},{\"name\":\"TUNE\"},{\"name\":\"FREQ\"},{\"name\":\"FIND\"}]', 2, 'LK0352', '“频率”的英文是 Frequency，前 4 个字母大写后即“FREQ”【BI6LAL】', '2022-06-02 10:04:55', '2022-06-02 10:20:21', 352);
INSERT INTO `q_bank` VALUES (900, 'a', '业余无线电通信常用缩语“GND”的意思是：', '[{\"name\":\"好运气\"},{\"name\":\"格林威治时间\"},{\"name\":\"地线，地面\"},{\"name\":\"高兴\"}]', 2, 'LK0361', '', '2022-06-02 10:04:55', '2022-06-02 10:20:21', 361);
INSERT INTO `q_bank` VALUES (901, 'a', '业余无线电通信常用缩语“OM”的意思是：', '[{\"name\":\"老朋友\"},{\"name\":\"欧姆\"},{\"name\":\"老人\"},{\"name\":\"或者\"}]', 0, 'LK0373', '', '2022-06-02 10:04:55', '2022-06-02 10:20:21', 373);
INSERT INTO `q_bank` VALUES (902, 'a', '“电台设备”的业余无线电通信常用缩语是：', '[{\"name\":\"RIG\"},{\"name\":\"SB\"},{\"name\":\"EQP\"},{\"name\":\"REG\"}]', 0, 'LK0376', '', '2022-06-02 10:04:55', '2022-06-02 10:20:21', 376);
INSERT INTO `q_bank` VALUES (903, 'a', '“收信机”的业余无线电通信常用缩语是：', '[{\"name\":\"XMTR\"},{\"name\":\"RCVR，RX\"},{\"name\":\"XCVR\"},{\"name\":\"RMKS\"}]', 1, 'LK0379', '', '2022-06-02 10:04:55', '2022-06-02 10:20:21', 379);
INSERT INTO `q_bank` VALUES (904, 'a', '“发信机”的业余无线电通信常用缩语是：', '[{\"name\":\"XTL\"},{\"name\":\"VXO\"},{\"name\":\"VXCO\"},{\"name\":\"TX、XMTR\"}]', 3, 'LK0380', '', '2022-06-02 10:04:56', '2022-06-02 10:20:21', 380);
INSERT INTO `q_bank` VALUES (905, 'a', '“收发信机”的业余无线电通信常用缩语是：', '[{\"name\":\"XVTR\"},{\"name\":\"XTL\"},{\"name\":\"XCVR\"},{\"name\":\"XMTR\"}]', 2, 'LK0381', '', '2022-06-02 10:04:56', '2022-06-02 10:20:21', 381);
INSERT INTO `q_bank` VALUES (906, 'a', '业余无线电通信常用缩语“WX”的意思是：', '[{\"name\":\"星期\"},{\"name\":\"天气\"},{\"name\":\"瓦特\"},{\"name\":\"联络、工作\"}]', 1, 'LK0396', '', '2022-06-02 10:04:56', '2022-06-02 10:20:21', 396);
INSERT INTO `q_bank` VALUES (907, 'a', '业余无线电通信常用缩语“73”的意思是：', '[{\"name\":\"向对方的致意、美好的祝愿\"},{\"name\":\"希望下次再见\"},{\"name\":\"谢谢你\"},{\"name\":\"再见\"}]', 0, 'LK0402', '', '2022-06-02 10:04:56', '2022-06-02 10:20:21', 402);
INSERT INTO `q_bank` VALUES (908, 'a', '业余无线电通话常用语“Roger”的用法是：', '[{\"name\":\"回答起始语，相当于“听到”，用于能听到对方信号、但不一定能全部抄收的情况\"},{\"name\":\"回答起始语，相当于“明白”，仅在已完全抄收对方刚才发送的信息时使用\"},{\"name\":\"惯用口头语，相当于电话的“喂”，仅提起注意，不包含任何意义\"},{\"name\":\"回答起始语，表示开始发话了，任何情况都可使用\"}]', 1, 'LK0404', '', '2022-06-02 10:04:56', '2022-06-02 10:20:21', 404);
INSERT INTO `q_bank` VALUES (909, 'a', '业余无线电通信中常用的天线种类的缩写DP代表：', '[{\"name\":\"长线天线\"},{\"name\":\"垂直天线\"},{\"name\":\"偶极天线\"},{\"name\":\"定向天线\"}]', 2, 'LK0405', '', '2022-06-02 10:04:57', '2022-06-02 10:20:21', 405);
INSERT INTO `q_bank` VALUES (910, 'a', '业余无线电通信中常用的天线种类的缩写GP代表：', '[{\"name\":\"偶极天线\"},{\"name\":\"对数周期天线\"},{\"name\":\"定向天线\"},{\"name\":\"垂直接地天线\"}]', 3, 'LK0407', '', '2022-06-02 10:04:57', '2022-06-02 10:20:21', 407);
INSERT INTO `q_bank` VALUES (911, 'a', '业余无线电通信中常用的天线种类的缩写BEAM代表：', '[{\"name\":\"垂直天线\"},{\"name\":\"偶极天线\"},{\"name\":\"专指八木天线\"},{\"name\":\"定向天线\"}]', 3, 'LK0408', '', '2022-06-02 10:04:57', '2022-06-02 10:20:21', 408);
INSERT INTO `q_bank` VALUES (912, 'a', '业余无线电通信中常用的天线种类的缩写YAGI代表：', '[{\"name\":\"定向天线\"},{\"name\":\"偶极天线\"},{\"name\":\"八木天线\"},{\"name\":\"垂直天线\"}]', 2, 'LK0409', '', '2022-06-02 10:04:57', '2022-06-02 10:20:21', 409);
INSERT INTO `q_bank` VALUES (913, 'a', '业余无线电通信中常用的天线种类的缩写VER代表：', '[{\"name\":\"垂直接地天线\"},{\"name\":\"偶极天线\"},{\"name\":\"定向天线\"},{\"name\":\"垂直天线\"}]', 3, 'LK0410', '', '2022-06-02 10:04:57', '2022-06-02 10:20:21', 410);
INSERT INTO `q_bank` VALUES (914, 'a', '可以组成完整无线电接收系统的功能部件组合是：', '[{\"name\":\"射频放大器、变频器、中频放大器\"},{\"name\":\"接收天线、解调器、输出部件\"},{\"name\":\"射频滤波器、变频器、音频滤波器\"},{\"name\":\"接收天线、射频放大器、中频放大器\"}]', 1, 'LK0416', '', '2022-06-02 10:04:57', '2022-06-02 10:20:21', 416);
INSERT INTO `q_bank` VALUES (915, 'a', '可以组成完整无线电发信系统的功能部件组合是：', '[{\"name\":\"话音放大器、射频振荡器、射频功率放大器\"},{\"name\":\"射频振荡器、射频功率放大器、驻波测量电路\"},{\"name\":\"键控电路、侧音电路、天线调谐器\"},{\"name\":\"射频振荡器、调制器、发射天线\"}]', 3, 'LK0417', '', '2022-06-02 10:04:58', '2022-06-02 10:20:21', 417);
INSERT INTO `q_bank` VALUES (916, 'a', '无线电发射机调制部件的作用是：', '[{\"name\":\"调整天馈系统的参数达到阻抗匹配\"},{\"name\":\"以原始信号控制射频信号的幅度、频率、相位参数\"},{\"name\":\"自动控制发射信号的频谱使其保持在核准的必要带宽范围内\"},{\"name\":\"以电能转换效率最高的方式控制射频功率放大器的工作点\"}]', 1, 'LK0418', '', '2022-06-02 10:04:58', '2022-06-02 10:20:21', 418);
INSERT INTO `q_bank` VALUES (917, 'a', '发射天线的作用是：', '[{\"name\":\"把无线电发射机输出的射频信号电流转换为热能\"},{\"name\":\"把无线电发射机放大后的音频话音信号转换为音频电磁场\"},{\"name\":\"通过天线的增益对无线电发射机输出的射频信号加以放大\"},{\"name\":\"把无线电发射机输出的射频信号电流转换为空间的电磁波\"}]', 3, 'LK0420', '', '2022-06-02 10:04:58', '2022-06-02 10:20:21', 420);
INSERT INTO `q_bank` VALUES (918, 'a', '接收天线系统的作用是：', '[{\"name\":\"把空间的有用电磁波转换为音频电压电流信号\"},{\"name\":\"把空间的有用电磁波转换为热能\"},{\"name\":\"通过天线的增益将空间有用电磁波的能量加以放大\"},{\"name\":\"把空间的有用电磁波转换为射频电压电流信号\"}]', 3, 'LK0421', '', '2022-06-02 10:04:58', '2022-06-02 10:20:21', 421);
INSERT INTO `q_bank` VALUES (919, 'a', '保证业余无线电通信接收机优良接收能力的主要因素是：', '[{\"name\":\"尽量宽的接收频率覆盖范围\"},{\"name\":\"尽量宽而平坦的音频频率响应\"},{\"name\":\"良好的抗干扰能力，足够高的灵敏度，尽量低的本机噪声和信号失真\"},{\"name\":\"尽量大的音频输出功率\"}]', 2, 'LK0422', '', '2022-06-02 10:04:58', '2022-06-02 10:20:21', 422);
INSERT INTO `q_bank` VALUES (920, 'a', '电流的单位是：', '[{\"name\":\"欧（姆）\"},{\"name\":\"瓦（特）\"},{\"name\":\"安（培）\"},{\"name\":\"伏（特）\"}]', 2, 'LK0431', '', '2022-06-02 10:04:59', '2022-06-02 10:20:21', 431);
INSERT INTO `q_bank` VALUES (921, 'a', '电压的单位是：', '[{\"name\":\"瓦（特）\"},{\"name\":\"伏（特）\"},{\"name\":\"安（培）\"},{\"name\":\"欧（姆）\"}]', 1, 'LK0432', '', '2022-06-02 10:04:59', '2022-06-02 10:20:21', 432);
INSERT INTO `q_bank` VALUES (922, 'a', '电阻的单位是：', '[{\"name\":\"伏（特）\"},{\"name\":\"安（培）\"},{\"name\":\"欧（姆）\"},{\"name\":\"瓦（特）\"}]', 2, 'LK0433', '', '2022-06-02 10:04:59', '2022-06-02 10:20:21', 433);
INSERT INTO `q_bank` VALUES (923, 'a', '功率的单位是：', '[{\"name\":\"安（培）\"},{\"name\":\"欧（姆）\"},{\"name\":\"瓦（特）\"},{\"name\":\"伏（特）\"}]', 2, 'LK0434', '', '2022-06-02 10:04:59', '2022-06-02 10:20:21', 434);
INSERT INTO `q_bank` VALUES (924, 'a', '用电压表检查一节干电池两端电压，未使用时测得1.5伏左右，用旧后测得1.2伏左右。正确的说法是：', '[{\"name\":\"旧干电池的电动势为1.35伏\"},{\"name\":\"旧干电池的电动势为0\"},{\"name\":\"旧干电池的电动势为1.5伏\"},{\"name\":\"旧干电池的电动势为1.2伏\"}]', 2, 'LK0436', '电动势没变，只是旧电池内阻增大【小K】', '2022-06-02 10:04:59', '2022-06-02 10:20:21', 436);
